export default function topic(topic={loading:true,data:[]},action){
    switch(action.type){
        case "topic_loading":
            return{
                loading:true,
                data:[]
            }

        case  "topic_loadingover":
            return {
                loading:false,
                data:action.data
            }
        default:
            return topic;
    }
}
