import React, { Fragment } from "react"
import {Card} from "antd"
import TopicTag from "../../component/topicTag";
export default function Details(props){
    let {loading,data}=props;
    let {author,content,create_at,good,top,tab,title,visite_count}=data
    return (
        <Card
        bordered
        loading={loading}
        title={
            <Fragment>
                <h1><TopicTag tab={top?"top":(good?"good":tab)}/>{title}</h1>
        <p>作者：<Link to={`/user/${author.loginname}`}>{author.loginname}</Link></p>
            </Fragment>
        } 
        >
            <div dangerouslySetInnerHTML={
                {__html:content}
            }></div>

        </Card>
    )
}
